import React, { useState } from 'react';
import './App.css';
import TitleComponent from './Components/TitleComponent';

function App() {
  const [titleText, setTitleText] = useState('Hello World!');
  const [inputText, setInputText] = useState('');

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleButtonClick = () => {
    setTitleText(inputText);
    setInputText(''); // Clear the input after updating the title
  };

  return (
    <div className="App">
      <TitleComponent id="tc" title={titleText}></TitleComponent>
      <form>
        <label>Enter new title:
          <input
            type="text"
            value={inputText}
            onChange={handleInputChange}
          />
        </label>
      </form>
      <button onClick={handleButtonClick}>click me to change title</button>
    </div>
  );
}

export default App;
